"use strict";const properties_class=class{constructor(){return{create_to_json:function(t,r={}){let e={};const n=t.split("\n");for(let t=0;t<n.length;t++){const r=n[t].split("=");if(r.length%2==0)for(let t=0;t<r.length;t+=2)try{e[r[t]]=r[t+1]}catch(t){throw Error("Error parsing to json.")}}return r.is_url?URL.createObjectURL(new Blob([JSON.stringify(e,null,2)],{type:"application/json"})):e},create_from_json:function(t,r={}){let e="";for(const[r,n]of Object.entries(t))e+=`${r}=${"object"==typeof n?JSON.stringify(n):n}\n`;return r.is_url?URL.createObjectURL(new Blob([e])):e}}}};
